import * as signalR from '@microsoft/signalr'; // import signalR
import { Subject } from 'rxjs';
import { Message } from '../models/Message';
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
export class ChatService {
    constructor(http) {
        this.http = http;
        this.POST_URL = "https://localhost:54921/api/chat/send";
        this.receivedMessageObject = new Message();
        this.sharedObj = new Subject();
        this.connection = new signalR.HubConnectionBuilder().withUrl('https://localhost:44368/Chatsocket').WithAutomaticReconnect().build();
        this.connection.start()
            .then(() => console.log('connection start'))
            .catch((err) => {
            console.log('Error while establishing the connection');
        });
        this.connection.start().then(function () {
            console.log('Connected!');
        }).catch(function (err) {
            return console.error(err.toString());
        });
        this.connection.onclose(async () => {
            await this.start();
        });
        this.connection.on("ReceiveOne", (user, message) => { this.mapReceivedMessage(user, message); });
        this.start();
    }
    // Strart the connection
    async start() {
        try {
            await this.connection.start();
            console.log("connected");
        }
        catch (err) {
            console.log(err);
            await this.connection.close();
            console.log("Disconnected");
            setTimeout(() => this.start(), 1000);
        }
    }
    mapReceivedMessage(user, message) {
        this.receivedMessageObject.user = user;
        this.receivedMessageObject.message = message;
        this.sharedObj.next(this.receivedMessageObject);
    }
    /* ****************************** Public Mehods **************************************** */
    // Calls the controller method
    broadcastMessage(msgDto) {
        this.http.post(this.POST_URL, msgDto).subscribe(data => console.log(data));
        // this.connection.invoke("SendMessage1", msgDto.user, msgDto.msgText).catch(err => console.error(err));    // This can invoke the server method named as "SendMethod1" directly.
    }
    retrieveMappedObject() {
        return this.sharedObj.asObservable();
    }
}
